import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import { useContent } from '../../context/ContentContext';
import { useNavigate } from 'react-router-dom';
import ContentDashboard from '../content/ContentDashboard';

/**
 * Dashboard Component following Single Responsibility Principle
 * Main dashboard view for authenticated users with statistics and overview
 */
const Dashboard = () => {
  const { user } = useAuth();
  const { contents, fetchAllContent } = useContent();
  const navigate = useNavigate();
  const [stats, setStats] = useState({
    total: 0,
    published: 0,
    drafts: 0,
    videos: 0,
    texts: 0,
    exercises: 0
  });

  useEffect(() => {
    fetchAllContent();
  }, []);

  useEffect(() => {
    if (contents.length > 0) {
      const newStats = {
        total: contents.length,
        published: contents.filter(c => c.published).length,
        drafts: contents.filter(c => !c.published).length,
        videos: contents.filter(c => c.type === 'VIDEO').length,
        texts: contents.filter(c => c.type === 'TEXT').length,
        exercises: contents.filter(c => c.type === 'INTERACTIVE_EXERCISE').length
      };
      setStats(newStats);
    }
  }, [contents]);

  const canManageContent = user && (user.role === 'INSTRUCTOR' || user.role === 'ADMIN');

  return (
    <div className="dashboard">
      <div className="container">
        <div className="dashboard-welcome">
          <h1>Welcome to Intelligent Tutoring System</h1>
          <p>
            Hello, {user?.firstName || user?.email}! 
            You're logged in as a <strong>{user?.role?.toLowerCase()}</strong>.
          </p>
        </div>

        {canManageContent && (
          <div className="dashboard-stats">
            <div className="stat-card">
              <div className="stat-icon" style={{background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'}}>üìö</div>
              <div className="stat-info">
                <h3>{stats.total}</h3>
                <p>Total Content</p>
              </div>
            </div>
            
            <div className="stat-card">
              <div className="stat-icon" style={{background: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)'}}>‚úÖ</div>
              <div className="stat-info">
                <h3>{stats.published}</h3>
                <p>Published</p>
              </div>
            </div>
            
            <div className="stat-card">
              <div className="stat-icon" style={{background: 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)'}}>üìù</div>
              <div className="stat-info">
                <h3>{stats.drafts}</h3>
                <p>Drafts</p>
              </div>
            </div>
            
            <div className="stat-card">
              <div className="stat-icon" style={{background: 'linear-gradient(135deg, #fa709a 0%, #fee140 100%)'}}>üé•</div>
              <div className="stat-info">
                <h3>{stats.videos}</h3>
                <p>Videos</p>
              </div>
            </div>

            <div className="stat-card">
              <div className="stat-icon" style={{background: 'linear-gradient(135deg, #30cfd0 0%, #330867 100%)'}}>üìÑ</div>
              <div className="stat-info">
                <h3>{stats.texts}</h3>
                <p>Text Content</p>
              </div>
            </div>

            <div className="stat-card">
              <div className="stat-icon" style={{background: 'linear-gradient(135deg, #a8edea 0%, #fed6e3 100%)'}}>üéØ</div>
              <div className="stat-info">
                <h3>{stats.exercises}</h3>
                <p>Exercises</p>
              </div>
            </div>
          </div>
        )}

        <div className="dashboard-content">
          <ContentDashboard />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
