import axios from 'axios';

const API_URL = 'http://localhost:8080/lessons';

/**
 * Lesson Service - Handles all lesson-related API calls
 */
const lessonService = {
  // Get all lessons
  getAllLessons: async () => {
    try {
      const response = await axios.get(API_URL);
      return response.data;
    } catch (error) {
      console.error('Error fetching lessons:', error);
      throw error;
    }
  },

  // Get lesson by ID
  getLessonById: async (id) => {
    try {
      const response = await axios.get(`${API_URL}/${id}`);
      return response.data;
    } catch (error) {
      console.error('Error fetching lesson:', error);
      throw error;
    }
  },

  // Get lessons by course ID
  getLessonsByCourse: async (courseId) => {
    try {
      const response = await axios.get(`${API_URL}/course/${courseId}`);
      return response.data;
    } catch (error) {
      console.error('Error fetching course lessons:', error);
      throw error;
    }
  },

  // Search lessons
  searchLessons: async (query) => {
    try {
      const response = await axios.get(`${API_URL}/search`, {
        params: { query }
      });
      return response.data;
    } catch (error) {
      console.error('Error searching lessons:', error);
      throw error;
    }
  },

  // Create new lesson
  createLesson: async (lessonData, token) => {
    try {
      const response = await axios.post(API_URL, lessonData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      return response.data;
    } catch (error) {
      console.error('Error creating lesson:', error);
      throw error;
    }
  },

  // Update lesson
  updateLesson: async (id, lessonData, token) => {
    try {
      const response = await axios.put(`${API_URL}/${id}`, lessonData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      return response.data;
    } catch (error) {
      console.error('Error updating lesson:', error);
      throw error;
    }
  },

  // Delete lesson
  deleteLesson: async (id, token) => {
    try {
      await axios.delete(`${API_URL}/${id}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
    } catch (error) {
      console.error('Error deleting lesson:', error);
      throw error;
    }
  }
};

export default lessonService;
