import React, { useState, useEffect } from 'react';
import { useContent } from '../../context/ContentContext';
import { useAuth } from '../../context/AuthContext';
import ContentForm from './ContentForm';
import './content.css';

/**
 * Content Dashboard Component following Single Responsibility Principle
 * Manages content display and operations
 */
const ContentDashboard = () => {
  const { 
    contents, 
    categories, 
    isLoading, 
    fetchAllContent, 
    searchContent,
    deleteContent,
    fetchCategories 
  } = useContent();
  
  const { user } = useAuth();
  const [searchQuery, setSearchQuery] = useState('');
  const [filterType, setFilterType] = useState('');
  const [filterStatus, setFilterStatus] = useState(''); // 'published', 'draft', or ''
  const [sortBy, setSortBy] = useState('newest'); // 'newest', 'oldest', 'title'
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [editingContent, setEditingContent] = useState(null);
  const [selectedContents, setSelectedContents] = useState([]);

  useEffect(() => {
    fetchAllContent();
    fetchCategories();
  }, []);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      await searchContent(searchQuery);
    } else {
      await fetchAllContent();
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this content?')) {
      const result = await deleteContent(id);
      if (result.success) {
        // Content already removed from state by context
      } else {
        alert(result.message);
      }
    }
  };

  const canEditContent = (content) => {
    if (!user) return false;
    return user.role === 'ADMIN' || 
           (user.role === 'INSTRUCTOR' && content.createdBy === user.id);
  };

  const canCreateContent = () => {
    return user && (user.role === 'INSTRUCTOR' || user.role === 'ADMIN');
  };

  // Apply filters and sorting
  let filteredContents = contents;
  
  // Filter by type
  if (filterType) {
    filteredContents = filteredContents.filter(content => content.type === filterType);
  }
  
  // Filter by status
  if (filterStatus === 'published') {
    filteredContents = filteredContents.filter(content => content.published === true);
  } else if (filterStatus === 'draft') {
    filteredContents = filteredContents.filter(content => content.published === false);
  }
  
  // Sort
  filteredContents = [...filteredContents].sort((a, b) => {
    if (sortBy === 'newest') {
      return new Date(b.createdDate) - new Date(a.createdDate);
    } else if (sortBy === 'oldest') {
      return new Date(a.createdDate) - new Date(b.createdDate);
    } else if (sortBy === 'title') {
      return a.title.localeCompare(b.title);
    }
    return 0;
  });

  if (isLoading) {
    return <div className="loading">Loading content...</div>;
  }

  return (
    <div className="content-dashboard">
      <div className="dashboard-header">
        <h2>Learning Content</h2>
        {canCreateContent() && (
          <button
            className="btn btn-primary"
            onClick={() => setShowCreateForm(true)}
          >
            Create New Content
          </button>
        )}
      </div>

      {/* Search and Filter Section */}
      <div className="content-controls">
        <form onSubmit={handleSearch} className="search-form">
          <input
            type="text"
            placeholder="Search content..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="form-control search-input"
          />
          <button type="submit" className="btn btn-secondary">
            üîç Search
          </button>
        </form>

        <div className="filter-group">
          <select
            value={filterType}
            onChange={(e) => setFilterType(e.target.value)}
            className="form-control filter-select"
          >
            <option value="">All Types</option>
            {categories.map(category => (
              <option key={category} value={category}>
                {category.replace('_', ' ')}
              </option>
            ))}
          </select>

          <select
            value={filterStatus}
            onChange={(e) => setFilterStatus(e.target.value)}
            className="form-control filter-select"
          >
            <option value="">All Status</option>
            <option value="published">Published</option>
            <option value="draft">Drafts</option>
          </select>

          <select
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            className="form-control filter-select"
          >
            <option value="newest">Newest First</option>
            <option value="oldest">Oldest First</option>
            <option value="title">Title (A-Z)</option>
          </select>
        </div>
      </div>

      {/* Content Summary */}
      <div className="content-summary">
        <p>Showing {filteredContents.length} of {contents.length} items</p>
        {canCreateContent() && selectedContents.length > 0 && (
          <div className="bulk-actions">
            <span>{selectedContents.length} selected</span>
            <button 
              className="btn btn-sm btn-danger"
              onClick={() => {
                if (window.confirm(`Delete ${selectedContents.length} selected items?`)) {
                  selectedContents.forEach(id => handleDelete(id));
                  setSelectedContents([]);
                }
              }}
            >
              üóëÔ∏è Delete Selected
            </button>
          </div>
        )}
      </div>

      {/* Content Grid */}
      <div className="content-grid">
        {filteredContents.length > 0 ? (
          filteredContents.map(content => (
            <div key={content.id} className={`content-card ${selectedContents.includes(content.id) ? 'selected' : ''}`}>
              {canEditContent(content) && (
                <input
                  type="checkbox"
                  className="content-checkbox"
                  checked={selectedContents.includes(content.id)}
                  onChange={(e) => {
                    if (e.target.checked) {
                      setSelectedContents([...selectedContents, content.id]);
                    } else {
                      setSelectedContents(selectedContents.filter(id => id !== content.id));
                    }
                  }}
                />
              )}
              
              <div className="content-header">
                <h3>{content.title}</h3>
                <span className={`content-type ${content.type.toLowerCase()}`}>
                  {content.type === 'TEXT' ? 'üìÑ' : content.type === 'VIDEO' ? 'üé•' : 'üéØ'}
                  {' '}{content.type.replace('_', ' ')}
                </span>
              </div>
              
              <div className="content-body">
                {content.type === 'TEXT' && (
                  <p className="content-preview">
                    {content.content?.substring(0, 150)}{content.content?.length > 150 ? '...' : ''}
                  </p>
                )}
                {content.type === 'VIDEO' && (
                  <div className="video-placeholder">
                    <div className="placeholder-icon">üé¨</div>
                    <p>Video Content</p>
                  </div>
                )}
                {content.type === 'INTERACTIVE_EXERCISE' && (
                  <div className="exercise-placeholder">
                    <div className="placeholder-icon">üéØ</div>
                    <p>Interactive Exercise</p>
                  </div>
                )}
              </div>
              
              <div className="content-footer">
                <div className="content-meta">
                  <span className="created-date">
                    üìÖ {new Date(content.createdDate).toLocaleDateString()}
                  </span>
                  <span className={`status ${content.published ? 'published' : 'draft'}`}>
                    {content.published ? '‚úÖ Published' : 'üìù Draft'}
                  </span>
                </div>
                
                {canEditContent(content) && (
                  <div className="content-actions">
                    <button
                      className="btn-icon btn-edit"
                      onClick={() => setEditingContent(content)}
                      title="Edit content"
                    >
                      ‚úèÔ∏è
                    </button>
                    <button
                      className="btn-icon btn-delete"
                      onClick={() => handleDelete(content.id)}
                      title="Delete content"
                    >
                      üóëÔ∏è
                    </button>
                  </div>
                )}
              </div>
            </div>
          ))
        ) : (
          <div className="no-content">
            <p>No content found.</p>
            {canCreateContent() && (
              <button
                className="btn btn-primary"
                onClick={() => setShowCreateForm(true)}
              >
                Create Your First Content
              </button>
            )}
          </div>
        )}
      </div>

      {/* Create/Edit Form Modal */}
      {(showCreateForm || editingContent) && (
        <div className="modal-overlay">
          <div className="modal-content">
            <ContentForm
              content={editingContent}
              onClose={() => {
                setShowCreateForm(false);
                setEditingContent(null);
              }}
              onSuccess={() => {
                setShowCreateForm(false);
                setEditingContent(null);
                fetchAllContent();
              }}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default ContentDashboard;
