import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../../context/AuthContext';
import courseService from '../../services/courseService';
import lessonService from '../../services/lessonService';
import topicService from '../../services/topicService';
import './CourseManagement.css';

/**
 * Course Management Component
 * Hierarchical structure: Courses → Lessons → Topics
 */
const CourseManagement = () => {
  const { user } = useContext(AuthContext);
  const [activeTab, setActiveTab] = useState('courses'); // courses, lessons, topics
  
  // Courses state
  const [courses, setCourses] = useState([]);
  const [showCourseModal, setShowCourseModal] = useState(false);
  const [editingCourse, setEditingCourse] = useState(null);
  const [courseForm, setCourseForm] = useState({
    title: '',
    description: '',
    difficulty: 'BEGINNER',
    published: false
  });
  
  // Lessons state
  const [lessons, setLessons] = useState([]);
  const [showLessonModal, setShowLessonModal] = useState(false);
  const [editingLesson, setEditingLesson] = useState(null);
  const [lessonForm, setLessonForm] = useState({
    title: '',
    description: '',
    courseId: '',
    orderIndex: 0,
    difficulty: 'BEGINNER',
    contentSummary: '',
    published: false
  });
  
  // Topics state
  const [topics, setTopics] = useState([]);
  const [showTopicModal, setShowTopicModal] = useState(false);
  const [editingTopic, setEditingTopic] = useState(null);
  const [topicForm, setTopicForm] = useState({
    name: '',
    description: '',
    courseId: '',
    published: false
  });
  
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');

  // Fetch all data
  useEffect(() => {
    fetchCourses();
    fetchLessons();
    fetchTopics();
  }, []);

  const fetchCourses = async () => {
    try {
      const data = await courseService.getAllCourses();
      setCourses(data);
    } catch (err) {
      setError('Failed to fetch courses');
    }
  };

  const fetchLessons = async () => {
    try {
      const data = await lessonService.getAllLessons();
      setLessons(data);
    } catch (err) {
      setError('Failed to fetch lessons');
    }
  };

  const fetchTopics = async () => {
    try {
      const data = await topicService.getAllTopics();
      setTopics(data);
    } catch (err) {
      setError('Failed to fetch topics');
    }
  };

  // Course CRUD operations
  const handleCreateCourse = () => {
    setCourseForm({
      title: '',
      description: '',
      difficulty: 'BEGINNER',
      published: false
    });
    setEditingCourse(null);
    setShowCourseModal(true);
  };

  const handleEditCourse = (course) => {
    setCourseForm({
      title: course.title,
      description: course.description,
      difficulty: course.difficulty || 'BEGINNER',
      published: course.published
    });
    setEditingCourse(course);
    setShowCourseModal(true);
  };

  const handleSaveCourse = async () => {
    setLoading(true);
    setError(null);
    try {
      if (editingCourse) {
        await courseService.updateCourse(editingCourse.id, courseForm, user.token);
        setSuccessMessage('Course updated successfully!');
      } else {
        await courseService.createCourse(courseForm, user.token);
        setSuccessMessage('Course created successfully!');
      }
      await fetchCourses();
      setShowCourseModal(false);
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (err) {
      setError('Failed to save course');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteCourse = async (id) => {
    if (!window.confirm('Delete this course? All associated lessons will also be affected.')) return;
    
    setLoading(true);
    try {
      await courseService.deleteCourse(id, user.token);
      setSuccessMessage('Course deleted successfully!');
      await fetchCourses();
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (err) {
      setError('Failed to delete course');
    } finally {
      setLoading(false);
    }
  };

  // Lesson CRUD operations
  const handleCreateLesson = () => {
    setLessonForm({
      title: '',
      description: '',
      courseId: courses.length > 0 ? courses[0].id : '',
      orderIndex: lessons.filter(l => l.courseId === courses[0]?.id).length + 1,
      difficulty: 'BEGINNER',
      contentSummary: '',
      published: false
    });
    setEditingLesson(null);
    setShowLessonModal(true);
  };

  const handleEditLesson = (lesson) => {
    setLessonForm({
      title: lesson.title,
      description: lesson.description,
      courseId: lesson.courseId,
      orderIndex: lesson.orderIndex,
      difficulty: lesson.difficulty || 'BEGINNER',
      contentSummary: lesson.contentSummary || '',
      published: lesson.published
    });
    setEditingLesson(lesson);
    setShowLessonModal(true);
  };

  const handleSaveLesson = async () => {
    setLoading(true);
    setError(null);
    try {
      if (editingLesson) {
        await lessonService.updateLesson(editingLesson.id, lessonForm, user.token);
        setSuccessMessage('Lesson updated successfully!');
      } else {
        await lessonService.createLesson(lessonForm, user.token);
        setSuccessMessage('Lesson created successfully!');
      }
      await fetchLessons();
      setShowLessonModal(false);
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (err) {
      setError('Failed to save lesson');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteLesson = async (id) => {
    if (!window.confirm('Delete this lesson?')) return;
    
    setLoading(true);
    try {
      await lessonService.deleteLesson(id, user.token);
      setSuccessMessage('Lesson deleted successfully!');
      await fetchLessons();
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (err) {
      setError('Failed to delete lesson');
    } finally {
      setLoading(false);
    }
  };

  // Topic CRUD operations
  const handleCreateTopic = () => {
    setTopicForm({
      name: '',
      description: '',
      courseId: courses.length > 0 ? courses[0].id : '',
      published: false
    });
    setEditingTopic(null);
    setShowTopicModal(true);
  };

  const handleEditTopic = (topic) => {
    setTopicForm({
      name: topic.name,
      description: topic.description,
      courseId: topic.courseId,
      published: topic.published
    });
    setEditingTopic(topic);
    setShowTopicModal(true);
  };

  const handleSaveTopic = async () => {
    setLoading(true);
    setError(null);
    try {
      if (editingTopic) {
        await topicService.updateTopic(editingTopic.id, topicForm, user.token);
        setSuccessMessage('Topic updated successfully!');
      } else {
        await topicService.createTopic(topicForm, user.token);
        setSuccessMessage('Topic created successfully!');
      }
      await fetchTopics();
      setShowTopicModal(false);
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (err) {
      setError('Failed to save topic');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteTopic = async (id) => {
    if (!window.confirm('Delete this topic?')) return;
    
    setLoading(true);
    try {
      await topicService.deleteTopic(id, user.token);
      setSuccessMessage('Topic deleted successfully!');
      await fetchTopics();
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (err) {
      setError('Failed to delete topic');
    } finally {
      setLoading(false);
    }
  };

  const getDifficultyBadge = (difficulty) => {
    const badges = {
      BEGINNER: { label: 'Beginner', className: 'badge-beginner' },
      INTERMEDIATE: { label: 'Intermediate', className: 'badge-intermediate' },
      ADVANCED: { label: 'Advanced', className: 'badge-advanced' },
      EXPERT: { label: 'Expert', className: 'badge-expert' }
    };
    const badge = badges[difficulty] || badges.BEGINNER;
    return <span className={`difficulty-badge ${badge.className}`}>{badge.label}</span>;
  };

  const getCourseName = (courseId) => {
    const course = courses.find(c => c.id === courseId);
    return course ? course.title : 'Unknown Course';
  };

  const getLessonCount = (courseId) => {
    return lessons.filter(l => l.courseId === courseId).length;
  };

  const getTopicCount = (courseId) => {
    return topics.filter(t => t.courseId === courseId).length;
  };

  return (
    <div className="course-management-container">
      <div className="management-header">
        <h1>Course Management</h1>
        <p className="header-subtitle">
          Manage your courses, lessons, and topics in one place
        </p>
      </div>

      {successMessage && (
        <div className="success-message">
          ✓ {successMessage}
        </div>
      )}

      {error && (
        <div className="error-message">
          ✗ {error}
        </div>
      )}

      {/* Tab Navigation */}
      <div className="tab-navigation">
        <button
          className={`tab-btn ${activeTab === 'courses' ? 'active' : ''}`}
          onClick={() => setActiveTab('courses')}
        >
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
            <path d="M2 3h6a4 4 0 0 1 4 4v14a3 3 0 0 0-3-3H2z"/>
            <path d="M22 3h-6a4 4 0 0 0-4 4v14a3 3 0 0 1 3-3h7z"/>
          </svg>
          Courses ({courses.length})
        </button>
        <button
          className={`tab-btn ${activeTab === 'lessons' ? 'active' : ''}`}
          onClick={() => setActiveTab('lessons')}
        >
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
            <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z"/>
            <polyline points="14 2 14 8 20 8"/>
          </svg>
          Lessons ({lessons.length})
        </button>
        <button
          className={`tab-btn ${activeTab === 'topics' ? 'active' : ''}`}
          onClick={() => setActiveTab('topics')}
        >
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
            <path d="M22 19a2 2 0 0 1-2 2H4a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h5l2 3h9a2 2 0 0 1 2 2z"/>
          </svg>
          Topics ({topics.length})
        </button>
      </div>

      {/* Courses Tab */}
      {activeTab === 'courses' && (
        <div className="tab-content">
          <div className="content-header">
            <h2>Courses</h2>
            <button className="btn-create" onClick={handleCreateCourse}>
              + Create Course
            </button>
          </div>

          <div className="cards-grid">
            {courses.map(course => (
              <div key={course.id} className="management-card">
                <div className="card-header">
                  <h3>{course.title}</h3>
                  {getDifficultyBadge(course.difficulty)}
                </div>
                <p className="card-description">{course.description}</p>
                <div className="card-stats">
                  <span className="stat">
                    📚 {getLessonCount(course.id)} lessons
                  </span>
                  <span className="stat">
                    📑 {getTopicCount(course.id)} topics
                  </span>
                  <span className={`status ${course.published ? 'published' : 'draft'}`}>
                    {course.published ? '✓ Published' : '○ Draft'}
                  </span>
                </div>
                <div className="card-actions">
                  <button className="btn-edit" onClick={() => handleEditCourse(course)}>
                    Edit
                  </button>
                  <button className="btn-delete" onClick={() => handleDeleteCourse(course.id)}>
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>

          {courses.length === 0 && (
            <div className="empty-state">
              <p>No courses yet. Create your first course to get started!</p>
            </div>
          )}
        </div>
      )}

      {/* Lessons Tab */}
      {activeTab === 'lessons' && (
        <div className="tab-content">
          <div className="content-header">
            <h2>Lessons</h2>
            <button className="btn-create" onClick={handleCreateLesson}>
              + Create Lesson
            </button>
          </div>

          <div className="cards-grid">
            {lessons.map(lesson => (
              <div key={lesson.id} className="management-card">
                <div className="card-header">
                  <h3>{lesson.title}</h3>
                  {getDifficultyBadge(lesson.difficulty)}
                </div>
                <p className="card-description">{lesson.description}</p>
                <div className="card-meta">
                  <span className="meta-item">
                    📚 {getCourseName(lesson.courseId)}
                  </span>
                  <span className="meta-item">
                    📊 Order: {lesson.orderIndex}
                  </span>
                  <span className={`status ${lesson.published ? 'published' : 'draft'}`}>
                    {lesson.published ? '✓ Published' : '○ Draft'}
                  </span>
                </div>
                {lesson.contentSummary && (
                  <p className="content-summary">{lesson.contentSummary}</p>
                )}
                <div className="card-actions">
                  <button className="btn-edit" onClick={() => handleEditLesson(lesson)}>
                    Edit
                  </button>
                  <button className="btn-delete" onClick={() => handleDeleteLesson(lesson.id)}>
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>

          {lessons.length === 0 && (
            <div className="empty-state">
              <p>No lessons yet. Create lessons within your courses!</p>
            </div>
          )}
        </div>
      )}

      {/* Topics Tab */}
      {activeTab === 'topics' && (
        <div className="tab-content">
          <div className="content-header">
            <h2>Topics</h2>
            <button className="btn-create" onClick={handleCreateTopic}>
              + Create Topic
            </button>
          </div>

          <div className="cards-grid">
            {topics.map(topic => (
              <div key={topic.id} className="management-card">
                <div className="card-header">
                  <h3>{topic.name}</h3>
                </div>
                <p className="card-description">{topic.description}</p>
                <div className="card-meta">
                  <span className="meta-item">
                    📚 {getCourseName(topic.courseId)}
                  </span>
                  <span className={`status ${topic.published ? 'published' : 'draft'}`}>
                    {topic.published ? '✓ Published' : '○ Draft'}
                  </span>
                </div>
                <div className="card-actions">
                  <button className="btn-edit" onClick={() => handleEditTopic(topic)}>
                    Edit
                  </button>
                  <button className="btn-delete" onClick={() => handleDeleteTopic(topic.id)}>
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>

          {topics.length === 0 && (
            <div className="empty-state">
              <p>No topics yet. Create topics to organize your lessons!</p>
            </div>
          )}
        </div>
      )}

      {/* Course Modal */}
      {showCourseModal && (
        <div className="modal-overlay" onClick={() => setShowCourseModal(false)}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <div className="modal-header">
              <h2>{editingCourse ? 'Edit Course' : 'Create New Course'}</h2>
              <button className="modal-close" onClick={() => setShowCourseModal(false)}>×</button>
            </div>
            <div className="modal-body">
              <div className="form-group">
                <label>Course Title *</label>
                <input
                  type="text"
                  value={courseForm.title}
                  onChange={e => setCourseForm({...courseForm, title: e.target.value})}
                  placeholder="e.g., Introduction to Programming"
                />
              </div>
              <div className="form-group">
                <label>Description *</label>
                <textarea
                  value={courseForm.description}
                  onChange={e => setCourseForm({...courseForm, description: e.target.value})}
                  placeholder="Describe what students will learn..."
                  rows="4"
                />
              </div>
              <div className="form-group">
                <label>Difficulty Level</label>
                <select
                  value={courseForm.difficulty}
                  onChange={e => setCourseForm({...courseForm, difficulty: e.target.value})}
                >
                  <option value="BEGINNER">Beginner</option>
                  <option value="INTERMEDIATE">Intermediate</option>
                  <option value="ADVANCED">Advanced</option>
                  <option value="EXPERT">Expert</option>
                </select>
              </div>
              <div className="form-group checkbox-group">
                <label>
                  <input
                    type="checkbox"
                    checked={courseForm.published}
                    onChange={e => setCourseForm({...courseForm, published: e.target.checked})}
                  />
                  Publish immediately
                </label>
              </div>
            </div>
            <div className="modal-footer">
              <button className="btn-cancel" onClick={() => setShowCourseModal(false)}>
                Cancel
              </button>
              <button
                className="btn-save"
                onClick={handleSaveCourse}
                disabled={loading || !courseForm.title || !courseForm.description}
              >
                {loading ? 'Saving...' : editingCourse ? 'Update Course' : 'Create Course'}
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Lesson Modal */}
      {showLessonModal && (
        <div className="modal-overlay" onClick={() => setShowLessonModal(false)}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <div className="modal-header">
              <h2>{editingLesson ? 'Edit Lesson' : 'Create New Lesson'}</h2>
              <button className="modal-close" onClick={() => setShowLessonModal(false)}>×</button>
            </div>
            <div className="modal-body">
              <div className="form-group">
                <label>Course *</label>
                <select
                  value={lessonForm.courseId}
                  onChange={e => setLessonForm({...lessonForm, courseId: e.target.value})}
                >
                  {courses.map(course => (
                    <option key={course.id} value={course.id}>{course.title}</option>
                  ))}
                </select>
              </div>
              <div className="form-group">
                <label>Lesson Title *</label>
                <input
                  type="text"
                  value={lessonForm.title}
                  onChange={e => setLessonForm({...lessonForm, title: e.target.value})}
                  placeholder="e.g., Variables and Data Types"
                />
              </div>
              <div className="form-group">
                <label>Description *</label>
                <textarea
                  value={lessonForm.description}
                  onChange={e => setLessonForm({...lessonForm, description: e.target.value})}
                  placeholder="Describe the lesson content..."
                  rows="3"
                />
              </div>
              <div className="form-group">
                <label>Content Summary</label>
                <textarea
                  value={lessonForm.contentSummary}
                  onChange={e => setLessonForm({...lessonForm, contentSummary: e.target.value})}
                  placeholder="Brief summary of what this lesson covers..."
                  rows="2"
                />
              </div>
              <div className="form-row">
                <div className="form-group">
                  <label>Order</label>
                  <input
                    type="number"
                    min="1"
                    value={lessonForm.orderIndex}
                    onChange={e => setLessonForm({...lessonForm, orderIndex: parseInt(e.target.value)})}
                  />
                </div>
                <div className="form-group">
                  <label>Difficulty Level</label>
                  <select
                    value={lessonForm.difficulty}
                    onChange={e => setLessonForm({...lessonForm, difficulty: e.target.value})}
                  >
                    <option value="BEGINNER">Beginner</option>
                    <option value="INTERMEDIATE">Intermediate</option>
                    <option value="ADVANCED">Advanced</option>
                    <option value="EXPERT">Expert</option>
                  </select>
                </div>
              </div>
              <div className="form-group checkbox-group">
                <label>
                  <input
                    type="checkbox"
                    checked={lessonForm.published}
                    onChange={e => setLessonForm({...lessonForm, published: e.target.checked})}
                  />
                  Publish immediately
                </label>
              </div>
            </div>
            <div className="modal-footer">
              <button className="btn-cancel" onClick={() => setShowLessonModal(false)}>
                Cancel
              </button>
              <button
                className="btn-save"
                onClick={handleSaveLesson}
                disabled={loading || !lessonForm.title || !lessonForm.description || !lessonForm.courseId}
              >
                {loading ? 'Saving...' : editingLesson ? 'Update Lesson' : 'Create Lesson'}
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Topic Modal */}
      {showTopicModal && (
        <div className="modal-overlay" onClick={() => setShowTopicModal(false)}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <div className="modal-header">
              <h2>{editingTopic ? 'Edit Topic' : 'Create New Topic'}</h2>
              <button className="modal-close" onClick={() => setShowTopicModal(false)}>×</button>
            </div>
            <div className="modal-body">
              <div className="form-group">
                <label>Course *</label>
                <select
                  value={topicForm.courseId}
                  onChange={e => setTopicForm({...topicForm, courseId: e.target.value})}
                >
                  {courses.map(course => (
                    <option key={course.id} value={course.id}>{course.title}</option>
                  ))}
                </select>
              </div>
              <div className="form-group">
                <label>Topic Name *</label>
                <input
                  type="text"
                  value={topicForm.name}
                  onChange={e => setTopicForm({...topicForm, name: e.target.value})}
                  placeholder="e.g., Loops and Iteration"
                />
              </div>
              <div className="form-group">
                <label>Description *</label>
                <textarea
                  value={topicForm.description}
                  onChange={e => setTopicForm({...topicForm, description: e.target.value})}
                  placeholder="Describe this topic..."
                  rows="4"
                />
              </div>
              <div className="form-group checkbox-group">
                <label>
                  <input
                    type="checkbox"
                    checked={topicForm.published}
                    onChange={e => setTopicForm({...topicForm, published: e.target.checked})}
                  />
                  Publish immediately
                </label>
              </div>
            </div>
            <div className="modal-footer">
              <button className="btn-cancel" onClick={() => setShowTopicModal(false)}>
                Cancel
              </button>
              <button
                className="btn-save"
                onClick={handleSaveTopic}
                disabled={loading || !topicForm.name || !topicForm.description || !topicForm.courseId}
              >
                {loading ? 'Saving...' : editingTopic ? 'Update Topic' : 'Create Topic'}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CourseManagement;
