import React, { useState, useEffect, useMemo } from 'react';
import { useContent } from '../../context/ContentContext';
import { useAuth } from '../../context/AuthContext';
import ContentForm from './ContentForm';
import './content.css';

/**
 * Content Dashboard Component following Single Responsibility Principle
 * Manages content display and operations with advanced filtering and bulk actions
 */
const ContentDashboard = () => {
  const { 
    contents, 
    categories, 
    isLoading, 
    fetchAllContent, 
    searchContent,
    deleteContent,
    fetchCategories 
  } = useContent();
  
  const { user } = useAuth();
  const [searchQuery, setSearchQuery] = useState('');
  const [filterType, setFilterType] = useState('');
  const [filterStatus, setFilterStatus] = useState(''); // all, published, draft
  const [sortBy, setSortBy] = useState('date-desc'); // date-desc, date-asc, title-asc, title-desc
  const [viewMode, setViewMode] = useState('grid'); // grid, list, stats
  const [selectedContents, setSelectedContents] = useState([]);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [editingContent, setEditingContent] = useState(null);

  useEffect(() => {
    fetchAllContent();
    fetchCategories();
  }, []);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      await searchContent(searchQuery);
    } else {
      await fetchAllContent();
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this content?')) {
      const result = await deleteContent(id);
      if (result.success) {
        // Content already removed from state by context
      } else {
        alert(result.message);
      }
    }
  };

  const canEditContent = (content) => {
    if (!user) return false;
    return user.role === 'ADMIN' || 
           (user.role === 'INSTRUCTOR' && content.createdBy === user.id);
  };

  const canCreateContent = () => {
    return user && (user.role === 'INSTRUCTOR' || user.role === 'ADMIN');
  };

  // Advanced filtering and sorting with useMemo for performance
  const filteredAndSortedContents = useMemo(() => {
    let filtered = [...contents];
    
    // Filter by type
    if (filterType) {
      filtered = filtered.filter(content => content.type === filterType);
    }
    
    // Filter by status
    if (filterStatus === 'published') {
      filtered = filtered.filter(content => content.published);
    } else if (filterStatus === 'draft') {
      filtered = filtered.filter(content => !content.published);
    }
    
    // Sort
    filtered.sort((a, b) => {
      switch (sortBy) {
        case 'date-desc':
          return new Date(b.createdDate) - new Date(a.createdDate);
        case 'date-asc':
          return new Date(a.createdDate) - new Date(b.createdDate);
        case 'title-asc':
          return a.title.localeCompare(b.title);
        case 'title-desc':
          return b.title.localeCompare(a.title);
        default:
          return 0;
      }
    });
    
    return filtered;
  }, [contents, filterType, filterStatus, sortBy]);

  // Statistics calculations
  const statistics = useMemo(() => {
    const total = contents.length;
    const published = contents.filter(c => c.published).length;
    const draft = total - published;
    const byType = {
      TEXT: contents.filter(c => c.type === 'TEXT').length,
      VIDEO: contents.filter(c => c.type === 'VIDEO').length,
      INTERACTIVE_EXERCISE: contents.filter(c => c.type === 'INTERACTIVE_EXERCISE').length
    };
    const myContent = contents.filter(c => c.createdBy === user?.id).length;
    
    return { total, published, draft, byType, myContent };
  }, [contents, user]);

  // Bulk actions
  const handleSelectContent = (contentId) => {
    setSelectedContents(prev => 
      prev.includes(contentId) 
        ? prev.filter(id => id !== contentId)
        : [...prev, contentId]
    );
  };

  const handleSelectAll = () => {
    if (selectedContents.length === filteredAndSortedContents.length) {
      setSelectedContents([]);
    } else {
      setSelectedContents(filteredAndSortedContents.map(c => c.id));
    }
  };

  const handleBulkDelete = async () => {
    if (selectedContents.length === 0) return;
    
    if (window.confirm(`Delete ${selectedContents.length} selected content(s)?`)) {
      for (const id of selectedContents) {
        await deleteContent(id);
      }
      setSelectedContents([]);
    }
  };

  const clearFilters = () => {
    setFilterType('');
    setFilterStatus('');
    setSortBy('date-desc');
    setSearchQuery('');
  };

  if (isLoading) {
    return <div className="loading">Loading content...</div>;
  }

  return (
    <div className="content-dashboard">
      <div className="dashboard-header">
        <div className="header-left">
          <h2>Learning Content</h2>
          <span className="content-count">{filteredAndSortedContents.length} items</span>
        </div>
        <div className="header-actions">
          <div className="view-toggle">
            <button 
              className={`view-btn ${viewMode === 'grid' ? 'active' : ''}`}
              onClick={() => setViewMode('grid')}
              title="Grid View"
            >
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <rect x="3" y="3" width="7" height="7"/>
                <rect x="14" y="3" width="7" height="7"/>
                <rect x="3" y="14" width="7" height="7"/>
                <rect x="14" y="14" width="7" height="7"/>
              </svg>
            </button>
            <button 
              className={`view-btn ${viewMode === 'list' ? 'active' : ''}`}
              onClick={() => setViewMode('list')}
              title="List View"
            >
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <line x1="8" y1="6" x2="21" y2="6"/>
                <line x1="8" y1="12" x2="21" y2="12"/>
                <line x1="8" y1="18" x2="21" y2="18"/>
                <line x1="3" y1="6" x2="3.01" y2="6"/>
                <line x1="3" y1="12" x2="3.01" y2="12"/>
                <line x1="3" y1="18" x2="3.01" y2="18"/>
              </svg>
            </button>
            <button 
              className={`view-btn ${viewMode === 'stats' ? 'active' : ''}`}
              onClick={() => setViewMode('stats')}
              title="Statistics"
            >
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <line x1="12" y1="20" x2="12" y2="10"/>
                <line x1="18" y1="20" x2="18" y2="4"/>
                <line x1="6" y1="20" x2="6" y2="16"/>
              </svg>
            </button>
          </div>
          {canCreateContent() && (
            <button
              className="btn btn-primary"
              onClick={() => setShowCreateForm(true)}
            >
              <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" style={{marginRight: '6px'}}>
                <line x1="12" y1="5" x2="12" y2="19"/>
                <line x1="5" y1="12" x2="19" y2="12"/>
              </svg>
              Create Content
            </button>
          )}
        </div>
      </div>

      {/* Statistics View */}
      {viewMode === 'stats' && (
        <div className="statistics-view">
          <div className="stat-card">
            <div className="stat-icon">ğŸ“š</div>
            <div className="stat-info">
              <div className="stat-value">{statistics.total}</div>
              <div className="stat-label">Total Content</div>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon">âœ…</div>
            <div className="stat-info">
              <div className="stat-value">{statistics.published}</div>
              <div className="stat-label">Published</div>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon">ğŸ“</div>
            <div className="stat-info">
              <div className="stat-value">{statistics.draft}</div>
              <div className="stat-label">Drafts</div>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon">ğŸ“„</div>
            <div className="stat-info">
              <div className="stat-value">{statistics.byType.TEXT}</div>
              <div className="stat-label">Text Content</div>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon">ğŸ¥</div>
            <div className="stat-info">
              <div className="stat-value">{statistics.byType.VIDEO}</div>
              <div className="stat-label">Videos</div>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon">ğŸ¯</div>
            <div className="stat-info">
              <div className="stat-value">{statistics.byType.INTERACTIVE_EXERCISE}</div>
              <div className="stat-label">Exercises</div>
            </div>
          </div>
          {canCreateContent() && (
            <div className="stat-card">
              <div className="stat-icon">ğŸ‘¤</div>
              <div className="stat-info">
                <div className="stat-value">{statistics.myContent}</div>
                <div className="stat-label">My Content</div>
              </div>
            </div>
          )}
        </div>
      )}

      {/* Search and Filter Section */}
      <div className="content-controls">
        <form onSubmit={handleSearch} className="search-form">
          <input
            type="text"
            placeholder="Search content..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="form-control search-input"
          />
          <button type="submit" className="btn btn-secondary">
            Search
          </button>
        </form>

        <select
          value={filterType}
          onChange={(e) => setFilterType(e.target.value)}
          className="form-control filter-select"
        >
          <option value="">All Types</option>
          {categories.map(category => (
            <option key={category} value={category}>
              {category.replace('_', ' ')}
            </option>
          ))}
        </select>

        <select
          value={filterStatus}
          onChange={(e) => setFilterStatus(e.target.value)}
          className="form-control filter-select"
        >
          <option value="">All Status</option>
          <option value="published">Published</option>
          <option value="draft">Draft</option>
        </select>

        <select
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
          className="form-control filter-select"
        >
          <option value="date-desc">Newest First</option>
          <option value="date-asc">Oldest First</option>
          <option value="title-asc">Title A-Z</option>
          <option value="title-desc">Title Z-A</option>
        </select>

        {(filterType || filterStatus || sortBy !== 'date-desc' || searchQuery) && (
          <button 
            className="btn btn-secondary clear-filters"
            onClick={clearFilters}
            title="Clear all filters"
          >
            Clear Filters
          </button>
        )}
      </div>

      {/* Bulk Actions Bar */}
      {canCreateContent() && selectedContents.length > 0 && (
        <div className="bulk-actions-bar">
          <div className="bulk-info">
            <input
              type="checkbox"
              checked={selectedContents.length === filteredAndSortedContents.length}
              onChange={handleSelectAll}
              id="select-all"
            />
            <label htmlFor="select-all">
              {selectedContents.length} selected
            </label>
          </div>
          <div className="bulk-buttons">
            <button 
              className="btn btn-danger btn-sm"
              onClick={handleBulkDelete}
            >
              Delete Selected
            </button>
            <button 
              className="btn btn-secondary btn-sm"
              onClick={() => setSelectedContents([])}
            >
              Cancel
            </button>
          </div>
        </div>
      )}

      {/* Content Grid */}
      <div className="content-grid">
        {filteredAndSortedContents.length > 0 ? (
          filteredAndSortedContents.map(content => (
            <div key={content.id} className="content-card">
              {canCreateContent() && canEditContent(content) && (
                <div className="card-checkbox">
                  <input
                    type="checkbox"
                    checked={selectedContents.includes(content.id)}
                    onChange={() => handleSelectContent(content.id)}
                    onClick={(e) => e.stopPropagation()}
                  />
                </div>
              )}
              <div className="content-header">
                <h3>{content.title}</h3>
                <span className={`content-type ${content.type.toLowerCase()}`}>
                  {content.type.replace('_', ' ')}
                </span>
              </div>
              
              <div className="content-body">
                {content.type === 'TEXT' && (
                  <p className="content-preview">
                    {content.content?.substring(0, 150)}...
                  </p>
                )}
                {content.type === 'VIDEO' && (
                  <div className="video-placeholder">
                    ğŸ“¹ Video Content
                  </div>
                )}
                {content.type === 'INTERACTIVE_EXERCISE' && (
                  <div className="exercise-placeholder">
                    ğŸ¯ Interactive Exercise
                  </div>
                )}
              </div>
              
              <div className="content-footer">
                <div className="content-meta">
                  <span className="created-date">
                    {new Date(content.createdDate).toLocaleDateString()}
                  </span>
                  <span className={`status ${content.published ? 'published' : 'draft'}`}>
                    {content.published ? 'Published' : 'Draft'}
                  </span>
                </div>
                
                {canEditContent(content) && (
                  <div className="content-actions">
                    <button
                      className="btn btn-sm btn-secondary"
                      onClick={() => setEditingContent(content)}
                    >
                      Edit
                    </button>
                    <button
                      className="btn btn-sm btn-danger"
                      onClick={() => handleDelete(content.id)}
                    >
                      Delete
                    </button>
                  </div>
                )}
              </div>
            </div>
          ))
        ) : (
          <div className="no-content">
            <p>No content found.</p>
            {canCreateContent() && (
              <button
                className="btn btn-primary"
                onClick={() => setShowCreateForm(true)}
              >
                Create Your First Content
              </button>
            )}
          </div>
        )}
      </div>

      {/* Create/Edit Form Modal */}
      {(showCreateForm || editingContent) && (
        <div className="modal-overlay">
          <div className="modal-content">
            <ContentForm
              content={editingContent}
              onClose={() => {
                setShowCreateForm(false);
                setEditingContent(null);
              }}
              onSuccess={() => {
                setShowCreateForm(false);
                setEditingContent(null);
                fetchAllContent();
              }}
            />
          </div>
        </div>
      )}
    </div>
  );
